mapper.select:
select count(id)
from members
where id=#{id}

mapper.insert:
insert into
HouseHolds(
household_seq
, member_seq
, product
, price
, get_date
,
content
, indate
, inuser
) values (
household_seq.nextval
, #{member_seq}
, #{product}
, #{price}
, sysdate
, #{content}
, sysdate
, #{inuser}
)

mapper.update:
update households
set
product=#{product}
, price=#{price}
, content=#{content}
, ldate=sysdate
where
household_seq=#{household_seq}

update members
set
id
=#{id}
, password = #{password}
, name=#{name}
, email=#{email}
,
phone_number=#{phone_number}
, postNumber=#{postNumber}
,
newAddress=#{newAddress}
, city=#{city}
, county=#{county}
,
village=#{village}
, ldate=sysdate
where
member_seq = #{member_seq}

update accounts set
account = #{account}
, montly_salary=#{montly_salary}
,
montly_save=#{montly_save}
, indate=sysdate
, inuser=#{inuser}
where
member_seq=#{member_seq}

mapper.select:
select account 
from accounts 
where member_seq=#{member_seq}
